%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%T = 0.01t = -2:T:10;degrau1= 1*((t-1)>=0);degrau2= -3*((t-4)>=0);degrau3= 2*((t-6)>=0);pulso = 2 * (t-3).*(((t-3)>=0)-((t-4)>=0));x_b = degrau1 + degrau2 + degrau3 + pulso%plot(t,x_b)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%EX1%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%(a) x(t) = (e^(-t))*(E(t))Ta = 0.01ta = -2:Ta:10;exponencial = exp(-ta);eletro = 1*(ta>=0);x1_a = exponencial.*eletro;%plot(ta,x1_a)%title('Exemplo 1.a')%xlabel('ta')%ylabel('x(ta)')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%(b) x(t) = r(t+2)-r(t-2)   , r(t)= tE(t)Tb = 0.01tb = -2:Tb:10;r1 = (tb+2).*((tb+2)>=0);r2 = (tb-2).*((tb-2)>=0);x1_b = r1.-r2;%plot(tb,x1_b)%title('Exemplo 1.b')%xlabel('tb')%ylabel('x(tb)')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%(c) x(t) = sinc((t/2)+1)   , sinc(t)= ((sin(pi*t))/pi*t)Tc = 0.01tc = -2:Tc:10;x1_c = sinc((t/2)+1);%plot(tc,x1_c)%title('Exemplo 1.c')%xlabel('tc')%ylabel('x(tc)')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%(d) x(t) = mod(sinc(t))Td = 0.01td = -2:Tc:10;x1_d = (sinc(t)>=0);%plot(td,x1_d)%title('Exemplo 1.d')%xlabel('td')%ylabel('x(td)')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%(e) x(t) = mod(sinc(t))Te = 0.01te = -2:Te:10;func1 = 1*((t-3)>=0);func2 = 1*((t+3)>=0);x1_e = func1.-func2;%plot(te,x1_e)%title('Exemplo 1.e')%xlabel('te')%ylabel('x(te)')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%(f) y(d) = e^(-t))*d(t)   , d(t)= x1_e(t)Tf = 0.01tf = -2:Tf:10;exponencial = exp(-tf);x1_f = exponencial.*x1_e;plot(tf,x1_f)title('Exemplo 1.f')xlabel('tf')ylabel('y(tf)')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%