%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%João Antunes nº62709 LEEC%Exercise I - (a)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function x = deg(a) x = 1*(a>0)endfunction x = imp(a) x = 1*(a==0)endt = 1;timedomain = 0:t:24*pi;x1 = cos((2*pi/8)*timedomain);x2 = rem(timedomain,6)==0;r = x1 + x2;stem(timedomain,r)title('Joao Antunes');ylabel('r(timedomain)');xlabel('timedomain');