%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%João Antunes nº62709 LEEC%Exercise II - (a)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%function x = deg(a) x = 1*(a>0)endfunction x = imp(a) x = 1*(a==0)endt = 1;timedomain = 0:t:24;x1 = cos((2*pi/8)*timedomain);x2 = rem(timedomain,6)==0;r = x1 + x2;impfunc = 1*imp(timedomain-1) + 1*imp(timedomain-2) + 1*imp(timedomain-3) + 1*imp(timedomain-4);ex2 = r.*impfunc;graphex2 = abs(ex2).^2;stem(timedomain,graphex2);title('Joao Antunes')ylabel('ex2(timedomain)')xlabel('timedomain')