%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%EX1%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%(a)%%%%%%%%%%%%%%%%%%%%%%function x=deg(a,deg_size) while (deg_size>0)  x = deg(a>=0);  deg_size = deg_size - 1; endwhileendfunction x=imp(a) x = 1*(a==0);endtimedomain = -10:10;signal = 2*imp(timedomain+8) + deg(timedomain+6,5);stem(timedomain,signal);title('Exemplo 1.a');xlabel('timedomain');ylabel('signal(timedomain)')%%%%%%%%%%%%%%%%%%%%%(a)%%%%%%%%%%%%%%%%%%%%%%timedomain = -10:10;signal = 3*imp(timedomain+8) + 1*deg(timedomain+6);signal = signal + 1*imp(timedomain+4) + 1*imp(timedomain+1) + 1*imp(timedomain);signal = signal + 1*imp(timedomain-1) - 1*imp(timedomain-5);stem(timedomain,signal);title('Exemplo 1.b');xlabel('timedomain');ylabel('signal(timedomain)')