%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%João Antunes nº62709 LEEC%Exercise I - (b)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% x(t) functiont = 0.01;timedomain = -5:t:5;x1 = 2*((timedomain+4)>=0);x2 = ((-2/3)*(timedomain+2)).*(((timedomain+2)>=0)-((timedomain-1)>=0));x3 = -2*((timedomain-1)>=0);x = x1+x2+x3;% r(t) functionargument = -2*timedomain-2;x1_a = 2*((argument+4)>=0);x2_a = ((-2/3)*(argument+2)).*(((argument+2)>=0)-((argument-1)>=0));x3_a = -2*((argument-1)>=0);r = 2*(x1_a + x2_a + x3_a);plot(timedomain,r);title('Joao Antunes')ylabel('r(timedomain)')xlabel('timedomain')